function send_mail(array $reservation_info): bool {
    $mail = new PHPMailer();
    $mail->isSMTP();
    $mail->CharSet = "UTF-8";
    $mail->Encoding = 'base64';
    $mail->SMTPDebug = false;
    //Set the hostname of the mail server
    $mail->Host = $_ENV['EMAIL_SERVER'];
    //Set the SMTP port number - 587 for authenticated TLS, a.k.a. RFC4409 SMTP submission
    $mail->Port = 587;
    $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;
    $mail->SMTPAuth = true;
    $mail->Username = $_ENV['EMAIL'];
    $mail->Password = $_ENV['EMAIL_PASSWORD'];

    $mail->setFrom($_ENV['EMAIL'], "Ταξιθέτες");
    $mail->addAddress($reservation_info["email"], $reservation_info["name"]);
    $mail->addBCC($_ENV['RESERVATION_MANAGER_MAIL'], $_ENV['RESERVATION_MANAGER_NAME']);
    $mail->Subject = "Κράτηση για την παράσταση «Ελέφαντας»";

    $body_message = VIEWER_MAIL;
    $body_message = str_replace("__DATE__", htmlspecialchars($reservation_info["date"]), $body_message);
    $body_message = str_replace("__NAME__", htmlspecialchars($reservation_info["name"]), $body_message);
    $body_message = str_replace("__PERSONS__", htmlspecialchars($reservation_info["persons"]), $body_message);
    $body_message = str_replace("__PHONE__", htmlspecialchars($reservation_info["phone"]), $body_message);
    $body_message = str_replace("__COMMENTS__", htmlspecialchars($reservation_info["comments"]), $body_message);
    $body_message = str_replace("__MANAGER_PHONE__", htmlspecialchars($_ENV['RESERVATION_MANAGER_PHONE']), $body_message);

    $mail->msgHTML($body_message);
    return $mail->send();
}

function process_request() {
    $data = json_decode(file_get_contents('php://input'), true);

    if (verify_captcha()) {
        if (send_mail($data)) {
            $status = 200;
        } else {
            $status = 500;
        }
    } else {
        $status = 400;
    }

    header('Content-Type: application/json');
    http_response_code($status);
    $responses = [
        200 => [
            "success" => true
        ],
        400 => [
            "success" => false,
            "error" => "Αποτυχία επαλήθευσης Captcha. Παρακαλούμε ξαναπροσπαθήστε.",
        ],
        500 => [
            "success" => false,
            "error" => "Προέκυψε ένα σφάλμα. Παρακαλούμε ξαναπροσπαθήστε.",
        ],
    ];
    echo json_encode($responses[$status]);
}

process_request();
